package com.daily.selfie.dailyselfieapp;import android.content.Context;import android.os.Environment;import java.io.File;import java.io.IOException;import java.text.SimpleDateFormat;import java.util.Date;public class Photo_Ticker {    public static File createImageFile(Context context) throws IOException {        String timeStamp = new SimpleDateFormat("yyyyMMdd_HHmmss_").format(new Date());        String imageFile = timeStamp;        File storDir = context.getExternalFilesDir(Environment.DIRECTORY_PICTURES);        File image = File.createTempFile(imageFile, ".jpg", storDir);        return image;    }}