package com.marcosapps.mgotado.dailyselfieapp.adapter;import android.content.Intent;import android.graphics.Bitmap;import android.graphics.BitmapFactory;import android.os.Bundle;import android.os.Environment;import android.util.Log;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.ImageView;import android.widget.TextView;import com.marcosapps.mgotado.dailyselfieapp.R;import com.marcosapps.mgotado.dailyselfieapp.view.ImageFull;import com.marcosapps.mgotado.dailyselfieapp.view.MainActivity;import java.io.ByteArrayOutputStream;import java.io.File;import java.io.FileOutputStream;import java.io.IOException;import java.sql.Date;import java.text.SimpleDateFormat;import java.util.List;public class PhotoAdapter extends BaseAdapter {    private MainActivity mainActivity;    private List<Bitmap> bitmaps;    private TextView mTextId;    private ImageView imagePhoto;    private String fullName;    public PhotoAdapter(MainActivity mainActivity, List<Bitmap> photo, String fullName) {        this.mainActivity = mainActivity;        this.bitmaps = photo;        this.fullName = fullName;    }    @Override    public int getCount() {        return bitmaps.size();    }    @Override    public Object getItem(int position) {        return bitmaps.get(position);    }    @Override    public long getItemId(int position) {        return position;    }    @Override    public View getView(final int position, View view, ViewGroup parent) {        final View mView = mainActivity.getLayoutInflater().inflate(R.layout.item_photo, parent,false);        final String shortName = fullName.substring(fullName.lastIndexOf('/') + 1, fullName.lastIndexOf('_'));        TextView mTextId = mView.findViewById(R.id.photoId);        ImageView imageView = mView.findViewById(R.id.image_photo);        mTextId.setText(shortName);        imageView.setImageBitmap(bitmaps.get(position));        imageView.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Intent intent = new Intent(mView.getContext(), ImageFull.class);                intent.putExtra("IDFoto", bitmaps.get(position));                mView.getContext().startActivity(intent);            }        });        return mView;    }}