package com.marcosapps.mgotado.dailyselfieapp.view;import android.content.Intent;import android.graphics.Bitmap;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.widget.ImageView;import com.marcosapps.mgotado.dailyselfieapp.R;public class ImageFull extends AppCompatActivity {    private ImageView imageView;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_image_full);        if (getSupportActionBar() != null) {            getSupportActionBar().hide();        }        Bitmap imgBitmap = (Bitmap) getIntent().getParcelableExtra("IDFoto");        ImageView imageView = findViewById(R.id.imageView);        imageView.setImageBitmap(imgBitmap);    }}